sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var curr_operation = current.operation();
	gs.addInfoMessage('curr_operation: ' + curr_operation);

	if (current.sent_to_accounting_date != '') {
		current.setAbortAction(true);
		if(gs.isInteractive()) {
			gs.addErrorMessage("Draw - " + current.number + " cannot be deleted because it has been sent to accounting");	
		}
	}

	if (current.isActionAborted() == 0) {
		if (current.approved_in_idis_date != '') {
			current.setAbortAction(true);
			if(gs.isInteractive()) {
				gs.addErrorMessage("Draw - " + current.number + " cannot be deleted because it has been approved in IDIS");	
			}
		}
	}

	if (current.isActionAborted() == 0) {
		if (current.entered_in_idis_date != '') {
			current.setAbortAction(true);
			if(gs.isInteractive()) {
				gs.addErrorMessage("Draw - " + current.number + " cannot be deleted because it has been entered in IDIS");	
			}
		}
	}

})(current, previous);
