sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var budget = new GlideRecord('x_ifa_homelessness_agency_budget');  
	budget.addQuery('agency_award', current.agency_award);
	budget.addQuery("sys_id","!=", current.sys_id);	
	budget.addQuery('budget_effective_date','<=', new GlideDate());	
	budget.orderByDesc('budget_effective_date');  
	budget.setLimit(1);  
	budget.query();

	while (budget.next()){
		if (current.budget_effective_date > budget.budget_effective_date) {
			// - Calculate the current budget amount drawn and budget amount available
			var gr = new GlideRecord('x_ifa_homelessness_agency_award');
			gr.addQuery('sys_id',current.agency_award.sys_id);
			gr.query();

			while (gr.next()){
				current.budget_drawn_amount = gr.grant_amount_drawn;
				current.budget_amount_available = Number(current.budget_total_amount) - Number(current.budget_drawn_amount);
				current.update();
			}

			// Calculate the budget item amount drawn and remaining
			var budgetItem = new GlideRecord('x_ifa_homelessness_agency_budget_item');  
			budgetItem.addQuery('agency_budget.sys_id', current.sys_id); 
			budgetItem.query(); 

			while (budgetItem.next()){
				var agg3 = new GlideAggregate('x_ifa_homelessness_agency_draw_item');
				agg3.addQuery('agency_draw.agency_award.sys_id',current.agency_award.sys_id);
				agg3.addQuery('budget_item.sys_id', budgetItem.budget_item.sys_id);
				agg3.addAggregate('SUM','item_amount');
				agg3.groupBy('agency_draw.agency_award');
				agg3.query();

				if(agg3.hasNext()) {
					while(agg3.next()){
						budgetItem.item_drawn_amount = Number(agg3.getAggregate('SUM','item_amount'));
					}			
				} else {
						budgetItem.item_drawn_amount = Number(0);			
				}
				budgetItem.item_amount_remaining = Number(budgetItem.item_amount) - Number(budgetItem.item_drawn_amount);
				budgetItem.update();
			}

		}
	}	

})(current, previous);
