sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.addInfoMessage('start rule');

	// -  Abort the transaction if the effective date is greater than the expiration date
	if (current.u_effective_date >= current.u_expiration_date) {
		current.setAbortAction(true);
		if(gs.isInteractive()) {
			gs.addErrorMessage("Effective Date " + current.u_effective_date.getDisplayValue() + " must be prior to the expiration date " + current.u_expiration_date.getDisplayValue());	
		}			
	}

	// -  Abort the transaction if the Amount allocated is greater than amount documented
	if (Number(current.u_amount_allocated) > Number(current.u_amount_documented)) {
		current.setAbortAction(true);
		if(gs.isInteractive()) {
			gs.addErrorMessage("Amount allocated " + current.u_amount_allocated.getDisplayValue() + " must be less than or equal to the amount documented " + current.u_amount_documented.getDisplayValue());	
		}			
	}

	// -  Abort the transaction if the effective date is less than a prior expiration date
	var lastm = new GlideRecord('x_ifa_homelessness_agency_match_history');  
	lastm.addQuery('u_agency_match.sys_id', current.u_agency_match.sys_id); 
	lastm.addQuery('sys_id', '!=', current.sys_id);
	lastm.orderByDesc('u_effective_date');  
	lastm.setLimit(1);  
	lastm.query();

	while (lastm.next()){
		if (current.u_effective_date > lastm.u_effective_date) {
			if (current.u_effective_date <= lastm.u_expiration_date) {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("Effective Date " + current.u_effective_date.getDisplayValue() + " must be greater than the previous expiration date " + lastm.u_expiration_date.getDisplayValue());	
				}			
			}

			// -  Abort the transaction if the allocated amount is less than the match amount drawn
			var match = new GlideRecord('x_ifa_homelessness_agency_match');  
			match.addQuery('sys_id', current.u_agency_match.sys_id); 
			match.query();

			while (match.next()){
				if (Number(current.u_amount_allocated) < Number(match.match_amount_drawn)) {
					current.setAbortAction(true);
					if(gs.isInteractive()) {
						gs.addErrorMessage("Allocated amount " + current.u_amount_allocated.getDisplayValue() + " must be greater than or equal to match amount drawn " + match.match_amount_drawn.getDisplayValue());	
					}			
				}
			}
		}
		else {
			current.setAbortAction(true);
			if(gs.isInteractive()) {
				gs.addErrorMessage("Effective Date " + current.u_effective_date.getDisplayValue() + " must be greater than the previous effective date " + lastm.u_effective_date.getDisplayValue());	
			}
		}
	}

})(current, previous);
