sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.addInfoMessage('Rule Started');

	var award = new GlideRecord('x_ifa_homelessness_agency_award');  
	award.addQuery('sys_id', current.u_agency_award.sys_id); 
	award.query();

	while (award.next()){
		// - Get the lastest agency grant history record
		var lastm = new GlideRecord('x_ifa_homelessness_agency_grant_history');  
		lastm.addQuery('u_agency_award.sys_id', current.u_agency_award.sys_id); 
		lastm.addQuery('sys_id', '!=', current.sys_id);
		lastm.orderByDesc('u_grant_change_date');  
		lastm.setLimit(1);  
		lastm.query();

		if(lastm.hasNext()) {
			while (lastm.next()){
				if (current.u_grant_change_date > lastm.u_grant_change_date) {
					award.grant_amount = current.u_grant_amount;
					award.grant_amount_available = Number(current.u_grant_amount) - Number(award.grant_amount_drawn);
				}
			}
		} 
		else
		{
			award.grant_amount = current.u_grant_amount;
			award.grant_amount_available = Number(current.u_grant_amount) - Number(award.grant_amount_drawn);
		}
		award.update();
	}	

})(current, previous);
