sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var draw = new GlideRecord('x_ifa_homelessness_agency_draw');  
	draw.addQuery('sys_id', current.agency_draw.sys_id);
	draw.query();

	// Abort the delete if an agency award exists
	while (draw.next()){	
		if (draw.sent_to_accounting_date != '') {
			current.setAbortAction(true);
			if(gs.isInteractive()) {
				gs.addErrorMessage("Match item - " + current.number + " cannot be deleted because the draw has been sent to accounting");	
			}
		}

		if (current.isActionAborted() == 0) {
			if (draw.approved_in_idis_date != '') {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("Match item - " + current.number + " cannot be deleted because the draw has been approved in IDIS");	
				}
			}
		}	

		if (current.isActionAborted() == 0) {
			if (draw.entered_in_idis_date != '') {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("Match item - " + current.number + " cannot be deleted because the draw has been entered in IDIS");	
				}
			}
		}	

		if (current.isActionAborted() == 0) {
			if (draw.management_approval_date != '') {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("Match item - " + current.number + " cannot be deleted because the draw has been approved by management");	
				}
			}
		}			
	}

})(current, previous);
