sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// -  Abort the transaction if the match amount drawn exceeds the match amount allocated
	var match = new GlideRecord('x_ifa_homelessness_agency_match');  
	match.addQuery('sys_id', current.agency_match.sys_id); 
	match.query();

	while (match.next()){

		var agg2 = new GlideAggregate('x_ifa_homelessness_agency_match_item');
		agg2.addQuery('agency_match.sys_id',current.agency_match.sys_id);
		agg2.addQuery('sys_id', '!=', current.sys_id);		
		agg2.addAggregate('SUM','item_amount');
		agg2.groupBy('agency_match');
		agg2.query();

		var matchDrawn = Number(0);

		if(agg2.hasNext()) {
			while(agg2.next()){
				matchDrawn = Number(agg2.getAggregate('SUM','item_amount')) + Number(current.item_amount);
			}			
		} else {
				matchDrawn = Number(current.item_amount);			
		}

		var matchDrawnl_display_value = matchDrawn.toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g, '$1,');

		if (matchDrawn > match.amount_allocated) {
			current.setAbortAction(true);
			if(gs.isInteractive()) {
				gs.addErrorMessage("Match amount drawn $" + matchDrawnl_display_value + " exceeds the match amount allocated " + match.amount_allocated.getDisplayValue());	
			}			
		}
	}

	if (current.isActionAborted() == false) {
		var draw = new GlideRecord('x_ifa_homelessness_agency_draw');  
		draw.addQuery('sys_id', current.agency_draw.sys_id); 
		draw.query();

		while (draw.next()){
			var rptMonths = draw.u_reporting_months.toString();
			var aRptMonths = rptMonths.split(",");

			for (var i=0; i < aRptMonths.length; i++) {
				var getDateStr = new GetDateString();
				var begin_date = getDateStr.FormatDateString(aRptMonths[i], draw.agency_award.grant_year_ref.getDisplayValue());

				var mHist = new GlideRecord('x_ifa_homelessness_agency_match_history');  
				mHist.addQuery('u_agency_match.sys_id', current.agency_match.sys_id);
				mHist.addQuery('u_expiration_date','>=', begin_date);

				begin_date.addMonthsLocalTime(1);
				begin_date.addDaysLocalTime(-1);

				mHist.addQuery('u_effective_date','<=', begin_date);
				mHist.query();

				if (mHist.hasNext() == false) {

					//current.setAbortAction(true);
					if(gs.isInteractive()) {
						gs.addErrorMessage("Match Source " + current.agency_match.getDisplayValue() + " was not active in " + aRptMonths[i]);
					}
				}
			}
		}
	}


})(current, previous);
