sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.addInfoMessage('Rule Started');

	// -  Find the latest agency match history record
	var lastm = new GlideRecord('x_ifa_homelessness_agency_match_history');  
	lastm.addQuery('u_agency_match.sys_id', current.u_agency_match.sys_id); 
	lastm.addQuery('sys_id', '!=', current.sys_id);
	lastm.orderByDesc('u_effective_date');  
	lastm.setLimit(1);  
	lastm.query();

	while (lastm.next()){
		if (current.u_effective_date > lastm.u_effective_date) {
			// -  Update the Match Allocated amount, amount documented, effective date, and expiration date
			var match = new GlideRecord('x_ifa_homelessness_agency_match');  
			match.addQuery('sys_id', current.u_agency_match.sys_id); 
			match.query();

			while (match.next()){
				match.amount_allocated = lastm.u_amount_allocated;
				match.match_amount_available = Number(lastm.u_amount_allocated) - Number(match.match_amount_drawn);
				match.amount_documented = lastm.u_amount_documented;
				match.expiration_date = lastm.u_expiration_date;
				match.u_effective_date = lastm.u_effective_date;
				match.update();
			}
		}
	}

})(current, previous);
