sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.addInfoMessage('Rule Started');

	// Update the Agency Award Match Drawn Amount and Match Amount Remaining
	var gr = new GlideRecord('x_ifa_homelessness_agency_award');
	gr.addQuery('sys_id',current.agency_match.agency_award.sys_id);
	gr.query();

	while (gr.next()){

		var agg1 = new GlideAggregate('x_ifa_homelessness_agency_match_item');
		agg1.addQuery('agency_match.agency_award.sys_id',current.agency_match.agency_award.sys_id);
		agg1.addAggregate('SUM','item_amount');
		agg1.groupBy('agency_match.agency_award');
		agg1.query();

		if(agg1.hasNext()) {
			while(agg1.next()){
				gr.match_amount_drawn = Number(agg1.getAggregate('SUM','item_amount'));
			}			
		} else {
				gr.match_amount_drawn = Number(current.item_amount);			
		}

		gr.match_amount_remaining = gr.match_required_amount - gr.match_amount_drawn;
		gr.update();
	}

	// - Calculate the agency match - match amount drawn
	var match = new GlideRecord('x_ifa_homelessness_agency_match');  
	match.addQuery('sys_id', current.agency_match.sys_id); 
	match.query(); 

	while (match.next()){
		var agg2 = new GlideAggregate('x_ifa_homelessness_agency_match_item');
		agg2.addQuery('agency_match.sys_id',current.agency_match.sys_id);
		agg2.addAggregate('SUM','item_amount');
		agg2.groupBy('agency_match');
		agg2.query();

		if(agg2.hasNext()) {
			while(agg2.next()){
				match.match_amount_drawn = Number(agg2.getAggregate('SUM','item_amount'));
			}			
		} else {
				match.match_amount_drawn = Number(current.item_amount);			
		}

		match.match_amount_available = match.amount_allocated - match.match_amount_drawn;		
		match.update();	
	}

})(current, previous);
