sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.addInfoMessage('start rule');

	var budget = new GlideRecord('x_ifa_homelessness_agency_budget');  
	budget.addQuery('sys_id', current.agency_budget.sys_id);
	budget.query();

	while (budget.next()){		
		//Get the total budget item amount for the current Budget
		var itemTot = new GlideAggregate('x_ifa_homelessness_agency_budget_item');
		itemTot.addQuery('agency_budget.sys_id', budget.sys_id);
		itemTot.addQuery('sys_id','!=',current.sys_id);
		itemTot.addAggregate('SUM','item_amount');
		itemTot.groupBy('agency_budget');
		itemTot.query();

		var budgetItemTotal = Number(current.item_amount);

		if(itemTot.hasNext()) {
			while(itemTot.next()){
				budgetItemTotal = Number(budgetItemTotal) +  Number(itemTot.getAggregate('SUM','item_amount'));
			}
		}

		var budgetItemTotal_display_value = budgetItemTotal.toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g, '$1,');

		if (budget.budget_effective_date == '') {
			// Make sure that budget total amount is greater than budget item total
			if (Number(budget.budget_total_amount) < Number(budgetItemTotal)) {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("The total of all budget items " + budgetItemTotal_display_value + " must be less than or equal to total Budget amount " + budget.budget_total_amount.getDisplayValue() );
				}				
			} 
		}
		else {
			// Make sure that budget total amount equals budget item total
			if (Number(budget.budget_total_amount) != Number(budgetItemTotal)) {			
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("The total of all budget items " + budgetItemTotal_display_value + " must be equal to total Budget amount " + budget.budget_total_amount.getDisplayValue() );
				}				
			} 
		}
	}

	// Get the total draw amount for the budget item
	var draw = new GlideAggregate('x_ifa_homelessness_agency_draw_item');
	draw.addQuery('agency_draw.agency_award.sys_id',current.agency_budget.agency_award.sys_id);
	draw.addQuery('item_amount','!=', 0);
	draw.addQuery('budget_item.sys_id', current.budget_item.sys_id);
	draw.addAggregate('SUM','item_amount');
	draw.groupBy('budget_item');
	draw.query();

	while(draw.next()){
		var draw_amount = Number(draw.getAggregate('SUM','item_amount'));
		var draw_display = draw_amount.toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g, '$1,');

		if (draw_amount != Number(0)) {						
			// Show error if a budget item with draw is not in the current budget
			if (current.item_amount < draw_amount) {
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage('Budget Item ' + draw.budget_item.getDisplayValue() + '  must have an Item Amount greater than or equal to the amount drawn ' + draw_display);	
				}
			}
		} 
	}	

})(current, previous);
