sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var match = new GlideRecord('x_ifa_homelessness_agency_match');  
	match.addQuery('sys_id', current.u_agency_match.sys_id); 
	match.query();

	while (match.next()){
		if (match.match_amount_drawn > Number(0)) {

			var lastm = new GlideRecord('x_ifa_homelessness_agency_match_history');  
			lastm.addQuery('u_agency_match.sys_id', current.u_agency_match.sys_id); 
			lastm.addQuery('sys_id', '!=', current.sys_id);
			lastm.orderByDesc('u_effective_date');  
			lastm.setLimit(1);  
			lastm.query();

			if (lastm.hasNext()) {
				while (lastm.next()){
					if (lastm.u_amount_allocated < match.match_amount_drawn) {
						current.setAbortAction(true);
						if(gs.isInteractive()) {
							gs.addErrorMessage("Match History cannot be deleted because the match drawn amount exceeds the amount allocated on the previous match history record");	
						}	
					}
					if (lastm.u_effective_date > current.u_effective_date) {
						current.setAbortAction(true);
						if(gs.isInteractive()) {
							gs.addErrorMessage("Match History cannot be deleted");	
						}	
					}
				}		
			} 
			else
			{
				current.setAbortAction(true);
				if(gs.isInteractive()) {
					gs.addErrorMessage("Match History cannot be deleted because there is a match amount drawn");	
				}	
			}
		}
	}


})(current, previous);
