sys_script_include.script:
function ref_qual_get_agency_budget_item() {

	//gs.addInfoMessage(current.agency_draw.agency_award.program_code.program_code);

	var result = 'sys_idIN';
	var ids1 = [];

	var budget = new GlideRecord('x_ifa_homelessness_agency_budget');  
	budget.addQuery('agency_award.sys_id', current.agency_draw.agency_award.sys_id); 
	budget.orderByDesc('budget_effective_date');  
	budget.setLimit(1);  
	budget.query(); 

	if (budget.next()) {  
     	var b_item = new GlideRecord('x_ifa_homelessness_agency_budget_item');
		b_item.addQuery('agency_budget.sys_id',budget.sys_id);
		b_item.query();
		while (b_item.next()){
			ids1.push('' + b_item.budget_item.sys_id);
		}
	}	

	//gs.addInfoMessage(ids1.toString());
	return result + ids1.toString();
}
