sys_script_include.script:
var GetDateString = Class.create();
GetDateString.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	initialize : function() {
	},

	FormatDateString : function(Month, Year) {
		var myDate = new GlideDate();

		if (Month == 'January') {
			myDate.setValue(Year + '-01-01');
			return myDate;
		}

		if (Month == 'February') {
			myDate.setValue(Year + '-02-01');
			return myDate;
		}

		if (Month == 'March') {
			myDate.setValue(Year + '-03-01');
			return myDate;
		}

		if (Month == 'April') {
			myDate.setValue(Year + '-04-01');
			return myDate;
		}

		if (Month == 'May') {
			myDate.setValue(Year + '-05-01');
			return myDate;
		}

		if (Month == 'June') {
			myDate.setValue(Year + '-06-01');
			return myDate;
		}

		if (Month == 'July') {
			myDate.setValue(Year + '-07-01');
			return myDate;
		}

		if (Month == 'August') {
			myDate.setValue(Year + '-08-01');
			return myDate;
		}

		if (Month == 'September') {
			myDate.setValue(Year + '-09-01');
			return myDate;
		}

		if (Month == 'October') {
			myDate.setValue(Year + '-10-01');
			return myDate;
		}

		if (Month == 'November') {
			myDate.setValue(Year + '-11-01');
			return myDate;
		}

		if (Month == 'December') {
			myDate.setValue(Year + '-12-01');
			return myDate;
		}
	},

    type: 'GetDateString'
});
