sys_ui_action.script:
function checkForActive() {
	alert('Clicked');
	current.update();
		alert('update');

	var draw = new GlideRecord('x_ifa_homelessness_agency_draw');  
	draw.addQuery('sys_id', current.agency_draw.sys_id); 
	draw.query();

	while (draw.next()){
		var rptMonths = draw.u_reporting_months.toString();
		var aRptMonths = rptMonths.split(",");

		for (var i=0; i < aRptMonths.length; i++) {
			var getDateStr = new GetDateString();
			var begin_date = getDateStr.FormatDateString(aRptMonths[i], draw.agency_award.grant_year_ref.getDisplayValue());

gs.addInfoMessage('begin date: ' + begin_date.getDisplayValue());

			var mHist = new GlideRecord('x_ifa_homelessness_agency_match_history');  
			mHist.addQuery('u_agency_match.sys_id', current.agency_match.sys_id);
			mHist.addQuery('u_expiration_date','>=', begin_date);

			begin_date.addMonthsLocalTime(1);
			begin_date.addDaysLocalTime(-1);

gs.addInfoMessage('end date: ' + begin_date.getDisplayValue());

			mHist.addQuery('u_effective_date','<=', begin_date);
			mHist.query();

			if (mHist.hasNext()) {
				gs.addInfoMessage('Active');
				current.update();
			}
			else {		
		gs.addInfoMessage('not active');
				//var ans = confirm('Are you sure? Match Source ' + current.agency_match.getDisplayValue() + ' was not active in ' + aRptMonths[i]);
					current.setAbortAction(true);
					if(gs.isInteractive()) {
						gs.addInfoMessage("Match Source " + current.agency_match.getDisplayValue() + " was not active in " + aRptMonths[i]);
					}

				var ans = confirm('Are you sure? Match Source ');
				gs.addInfoMessage('confirmed');
				if(ans == true){
					current.update();
				}
				else {
					current.setAbortAction(true);
					if(gs.isInteractive()) {
						gs.addInfoMessage("Match Source " + current.agency_match.getDisplayValue() + " was not active in " + aRptMonths[i]);
					}
				}
			}
		}
	}
}
